import React from "react";

const Footer = () => {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // Updated styles for the footer components with gradient and shadow
  const footerStyle = {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    background: "linear-gradient(to right, #667db6, #0082c8, #0082c8, #667db6)", // Adding gradient background
    boxShadow: "0 3px 6px 0 rgba(0, 0, 0, 0.23)", // Adding shadow effect
    color: "white", // Adjust text color so it's visible against the dark background
  };

  const buttonStyle = {
    cursor: "pointer",
    backgroundColor: "transparent",
    border: "none",
    color: "white", // Adjust button text color so it's visible against the dark background
    textDecoration: "underline",
    padding: "0",
    fontSize: "inherit",
    lineHeight: "inherit",
    outline: "none", // Remove focus border that browsers put around buttons by default
  };

  return (
    <footer style={footerStyle}>
      <div>Â© 2023 Haris Musa. All rights reserved.</div>
      <button onClick={scrollToTop} style={buttonStyle}>
        Back to Top
      </button>
    </footer>
  );
};

export default Footer;
