import React, { useEffect, useState } from "react";

const TypingAnimation = ({
  strings,
  typeSpeed = 100,
  backspaceSpeed = 50,
  delayAfterCompletion = 1000,
}) => {
  const [text, setText] = useState("");
  const [arrayIndex, setArrayIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const typeCharacter = () => {
      if (!isDeleting) {
        setText((prev) => prev + strings[arrayIndex].charAt(charIndex));
        setCharIndex((prev) => prev + 1);
      }
    };

    const deleteCharacter = () => {
      if (isDeleting) {
        setText((prev) => prev.substring(0, prev.length - 1));
        setCharIndex((prev) => prev - 1);
      }
    };

    if (arrayIndex >= strings.length) {
      setArrayIndex(0);
      return;
    }

    if (charIndex === strings[arrayIndex].length && !isDeleting) {
      setTimeout(() => setIsDeleting(true), delayAfterCompletion);
      return;
    }

    if (charIndex === 0 && isDeleting) {
      setIsDeleting(false);
      setArrayIndex((prev) => (prev + 1) % strings.length);
      return;
    }

    const timerId = setTimeout(
      () => {
        if (isDeleting) {
          deleteCharacter();
        } else {
          typeCharacter();
        }
      },
      isDeleting ? backspaceSpeed : typeSpeed
    );

    return () => clearTimeout(timerId);
  }, [
    charIndex,
    arrayIndex,
    isDeleting,
    strings,
    typeSpeed,
    backspaceSpeed,
    delayAfterCompletion,
    typeCharacter,
    deleteCharacter,
  ]);

  return <span className="typing">{text || "\u00A0"}</span>;
};

export default TypingAnimation;
