import React from "react";
import { useCallback } from "react";
import Particles from "react-particles";
import { loadSlim } from "tsparticles-slim";
import { MagnifyingGlassIcon } from "@heroicons/react/24/solid";
import TypingAnimation from "./TypingAnimation";

function Hero() {
  const particlesInit = useCallback(async (engine) => {
    console.log(engine);
    await loadSlim(engine);
  }, []);

  const particlesLoaded = useCallback(async (container) => {
    await console.log(container);
  }, []);
  return (
    <>
      <section
        className="flex  text-gray-600 body-font bg-gradient-to-r from-violet-500 to-fuchsia-500 z-0 p-[12%] relative  w-[100%] mx-auto h-[100%] gap-10 max-[700px]:flex-col items-center "
        id="hero"
      >
        <Particles
          id="tsparticles"
          init={particlesInit}
          loaded={particlesLoaded}
          options={{
            background: {
              color: {
                value: "",
              },
            },
            fpsLimit: 300,
            interactivity: {
              events: {
                onClick: {
                  enable: true,
                  mode: "push",
                },
                onHover: {
                  enable: false,
                  mode: "repulse",
                },
                resize: false,
              },
              modes: {
                push: {
                  quantity: 2,
                },
                repulse: {
                  distance: 2,
                  duration: 60,
                },
              },
            },
            particles: {
              color: {
                value: "#ffffff",
              },
              links: {
                color: "#ffffff",
                distance: 150,
                enable: true,
                opacity: 0.5,
                width: 1,
              },
              move: {
                direction: "none",
                enable: true,
                outModes: {
                  default: "bounce",
                },
                random: false,
                speed: 5,
                straight: false,
              },
              number: {
                density: {
                  enable: true,
                  area: 1000,
                },
                value: 80,
              },
              opacity: {
                value: 0.5,
              },
              shape: {
                type: "circle",
              },
              size: {
                value: { min: 1, max: 3 },
              },
            },
            detectRetina: true,
          }}
        />{" "}
        <div className="container mx-auto flex flex-col px-5 py-24 justify-center items-center ">
          <div className="w-full md:w-2/3 flex flex-col mb-16 items-center text-center">
            <h1 className="title-font sm:text-8xl text-4xl mb-4 font-medium text-white max-[1000px]:w-[100%]">
              Find out the truth!
            </h1>
            <p className="mb-8 leading-relaxed text-white ">
              ClaimCracker provides a way to quickly find out about truthfulness
              of the claims made by
              <div className="">
                <br />
              </div>
              <TypingAnimation
                className="md:text-5xl sm:text-4xl text-xl font-bold md:pl-4 pl-2"
                strings={["Politicians", "Celebrities", "Influencers"]}
                typeSpeed={120}
                delay={2000}
              />
            </p>
            <div className="relative  justify-center max-[1000px]:w-[100%] w-[100%] ">
              <div className="relative   text-left  ">
                <input
                  className="w-[100%] p-[5%]  flex bg-inherit border-white rounded-full focus:outline-none text-sm py-3 text-white placeholder-white       border-[1px]"
                  type="text"
                  placeholder={"  Search"}
                />
              </div>
              <MagnifyingGlassIcon className="absolute w-8  right-[15px] top-0  text-white  group-hover:bg-gray-200  h-full flex items-center justify-center" />
            </div>
            <p className="text-sm mt-2 text-gray-200 mb-8 w-full">
              Want to check out how we evaluate claims ?
            </p>
            <div className="flex">{}</div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Hero;
