import React from "react";

const Footer = () => {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  // Inline styles for the footer components
  const footerStyle = {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#f2f2f2",
    borderTop: "1px solid #e0e0e0",
    fontSize: "0.9rem",
    lineHeight: "1.6",
    color: "#333",
    fontFamily: "Arial, sans-serif",
  };

  const buttonStyle = {
    cursor: "pointer",
    backgroundColor: "transparent",
    border: "none",
    color: "blue",
    textDecoration: "underline",
    padding: "0",
    fontSize: "inherit",
    lineHeight: "inherit",
  };

  return (
    <footer style={footerStyle}>
      <div>Â© 2023 Haris Musa. All rights reserved.</div>
      <button onClick={scrollToTop} style={buttonStyle}>
        Back to Top
      </button>
    </footer>
  );
};

export default Footer;
